package care.data4life.securestore

import androidx.test.platform.app.InstrumentationRegistry
import org.junit.After
import org.junit.Before
import org.junit.Ignore
import org.junit.Test
import kotlin.test.assertFalse
import kotlin.test.assertNull
import kotlin.test.assertTrue

class SecureStoreInstrumentedTest {

    private lateinit var cryptor: SecureStoreCryptor
    private lateinit var storage: SecureStoreStorage

    // SUT
    private lateinit var secureStore: SecureStore

    @Before
    fun setup() {
        val context = InstrumentationRegistry.getInstrumentation().context

        cryptor = SecureStoreCryptor(context)
        storage = SecureStoreStorage(context)

        secureStore = SecureStore(cryptor, storage)
    }

    @Test
    fun verify_functionality() {
        // verify adding data
        secureStore.addData(dataAlias, data)

        assertTrue(secureStore.containsData(dataAlias))
        assertTrue { data.contentEquals(secureStore.getData(dataAlias)!!) }

        // verify removal
        secureStore.removeData(dataAlias)

        assertFalse(secureStore.containsData(dataAlias))
        assertNull(secureStore.getData(dataAlias))

        // verify adding long data
        secureStore.addData(longDataAlias, longData)

        assertTrue(secureStore.containsData(longDataAlias))
        assertTrue(longData.contentEquals(secureStore.getData(longDataAlias)!!))

        // verify adding multiple
        secureStore.addData(dataAlias, data)

        assertTrue(secureStore.containsData(longDataAlias))
        assertTrue(longData.contentEquals(secureStore.getData(longDataAlias)!!))
        assertTrue(secureStore.containsData(dataAlias))
        assertTrue { data.contentEquals(secureStore.getData(dataAlias)!!) }

        // verify replace existing data
        secureStore.addData(dataAlias, dataNew)

        assertTrue(secureStore.containsData(dataAlias))
        assertTrue { dataNew.contentEquals(secureStore.getData(dataAlias)!!) }

        // verify clear
        secureStore.clear()

        assertFalse(secureStore.containsData(longDataAlias))
        assertNull(secureStore.getData(longDataAlias))
        assertFalse(secureStore.containsData(dataAlias))
        assertNull(secureStore.getData(dataAlias))
    }

    @After
    fun cleanup() {
        secureStore.clear()
    }

